// This is the width of the Max or Min labels
#define k_textContentWidth 36

// This is the height of the Max or Min labels
#define k_textContentHeight 20

// this is an additional vertical offset for the min text to adjust it to the right vertical coordinate
#define k_minTextTextOffset 15

// This is the middle value for the slider
#define k_sliderMidValue 50

// this is the vertical offset of the tick view from the gauge view (also applies to the triangle view)
#define k_verticalOffsetForTickView 20

// this is the horizontal offset of the tick view from the gauge view (also applies to the triangle view)
#define k_horizontalOffsetForTickView 20

// this is the vertical offset of the triangle view from the gauge view 
#define k_verticalOffsetForTriangleView 20

// this is the horizontal offset of the triangle view from the gauge view 
#define k_horizontalOffsetForTriangleView 20

// this is the vertical offset of the traingle shadow from the triangle itself
#define k_verticalOffsetForTriangleShadowView 1

// this is the horizontal offset of the traingle shadow from the triangle itself
#define k_horizontalOffsetForTriangleShadowView 1

// This is in case an additional vertical offset needs to be given to the Max label (a positive offset will move the label upwards)
#define k_verticalOffsetForMaxText -8

// This is in case an additional vertical offset needs to be given to the Min label (a positive offset will move the label downwards)
#define k_verticalOffsetForMinText -5

